mapper.select:
select ru.dt_no, r_no, email, r_name, r_phone, r_people, r_memo
from res_user ru
join res_set rs
on ru.dt_no = rs.dt_no
where ru.dt_no=#{dt_no}
and s_no=#{s_no};

mapper.delete:
delete
from res_user
where r_no=#{r_no}
and dt_no=#{dt_no}

mapper.select:
select r_people
from res_user
where r_no=#{r_no}
and dt_no=#{dt_no}

mapper.update:
update res_user
set r_name=#{r_name},r_phone=#{r_phone},r_people=#{r_people},r_memo=#{r_memo}
where r_no=#{r_no}
and dt_no=#{dt_no}
